import React from 'react';
import {Navbar,Nav} from 'react-bootstrap';
import {withRouter} from 'react-router-dom';
import {connect} from 'react-redux';
import logoutUserAction from './../Redux/Actions/LogoutUser';
import { items } from "./../Stitch/mongodb";

class Menu extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            isAdmin: false,
            isDealer: false,
            isCustomer: false
        }
    }
    
    handleLogout = (e) => {
        e.preventDefault();
        //calling the logout action creator 
        this.props.logoutUserAction();
    }

    async componentDidMount() {
        //storing current user details
        var curruser = this.props.isLogIn.userReducer.currentuser;
        //getting data from db
        var details = await items.find({ "owner_id": curruser.id }).toArray();
        //checking if the user details are present  in db or not
        //var ispresent = details.some(item => item.owner_id === curruser.id);
        if (details) {
            //setting the state values
            this.setState({
                isAdmin: details[0].isAdmin,
                isCustomer: details[0].isCustomer,
                isDealer: details[0].isDealer
            })
        }
    }

    render() {
        //storing boolean value of isLoggedInUesr
        var check = this.props.isLogIn.userReducer.check;
   
        return(
            <Navbar bg="info" variant="light" sticky="top" >
                <Navbar.Brand href="/">Know The World</Navbar.Brand>
                <Nav className="mr-auto" style={{ paddingLeft: '370px' }}>
                    <Nav.Link href="/" style={{ fontWeight: 'bold' }}>Home</Nav.Link>
                    {/* Showing and Hiding Links depending on whether a user is logged in or not */}
                    {check && this.state.isCustomer && < Nav.Link href='/cities' style={{ fontWeight: 'bold' }}>Wonders</Nav.Link>}
                    {check && !this.state.isCustomer && <Nav.Link href='/buy_tickets' style={{ fontWeight: 'bold' }}>Tickets</Nav.Link>}
                    {check && this.state.isCustomer && <Nav.Link href='/profile' style={{ fontWeight: 'bold' }}>Profile</Nav.Link>}
                    {check && this.state.isCustomer && <Nav.Link style={{ fontWeight: 'bold' }} onClick={this.handleLogout} >Logout</Nav.Link>}
                    {!check && <Nav.Link href='/register' style={{ fontWeight: 'bold' }}>Register</Nav.Link>}
                    {!check && <Nav.Link href='/login' style={{ fontWeight: 'bold' }}>LogIn</Nav.Link>}
                </Nav>
            </Navbar>
        );
    }
}

//Accessing the state of the reducer 
const mapStateToProps = (state) => ({
        isLogIn: state      
})
//using action to logout user
const mapDispatchToProps = {
    logoutUserAction
}

export default connect(mapStateToProps,mapDispatchToProps)(withRouter(Menu));
