import 'primeicons/primeicons.css';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primereact.css';
import 'primeflex/primeflex.css';

import React, { Component } from 'react';
import {Sidebar} from 'primereact/sidebar';
import {Button} from 'primereact/button';

export default class Menu extends Component {

    constructor() {
        super();
        this.state = {
            visibleLeft: false,
            isAdmin: false,
            isDealer: false,
            isCustomer: false
        };
    }

    handleLogout = (e) => {
        e.preventDefault();
        //calling the logout action creator 
        this.props.logoutUserAction();
    }

    async componentDidMount() {
        //storing current user details
        var curruser = this.props.reducer_state.userReducer.currentuser;
        var dealer = this.props.reducer_state.userReducer.dealer;
        var customer = this.props.reducer_state.userReducer.customer;
        //getting data from db
        if (curruser != null) {
            var details = await items.find({}).toArray();
            //checking if the user details are present  in db or not
            var ispresent = details.some(item => item.owner_id === curruser.id);
           // console.log("checking is dealer:- " + dealer);
            var result = details.filter(item => item.owner_id === curruser.id);
            if (ispresent) {
                //setting the state values
                this.setState({
                    isAdmin: result[0].isAdmin,
                    isCustomer: result[0].isCustomer,
                    isDealer: result[0].isDealer
                })
            }
            else{
                //setting the state values
                this.setState({
                    isCustomer: customer,
                    isDealer: dealer
                })
            }
        }
    }

    render() {
        return (
            <div>
                <Sidebar visible={this.state.visibleLeft} baseZIndex={1000000} onHide={() => this.setState({visibleLeft: false})}>
                    <h1 style={{fontWeight:'normal'}}>Left Sidebar</h1>
                    <Button type="button" onClick={(e) => this.setState({visibleLeft: false})} label="Save" className="p-button-success" style={{marginRight:'.25em'}} />
                    <Button type="button" onClick={(e) => this.setState({visibleLeft: false})} label="Cancel" className="p-button-secondary"/>
                </Sidebar>



                <Button icon="pi pi-arrow-right" onClick={(e) => this.setState({visibleLeft:true})}  style={{marginRight:'0em'}} />

            </div>
        )
    }
}
