import React, { Component } from "react";
import { BrowserRouter as Router } from "react-router-dom";
import { MDBSideNavCat, MDBSideNavNav, MDBSideNav, MDBSideNavLink, MDBContainer, MDBIcon, MDBBtn } from "mdbreact";

class SideNavPage extends Component {
  state = {
    sideNavLeft: false,
    isAdmin: false,
    isDealer: false,
    isCustomer: false
  }

sidenavToggle = sidenavId => () => {
  const sidenavNr = `sideNav${sidenavId}`
  this.setState({
    [sidenavNr]: !this.state[sidenavNr]
  });
};

handleLogout = (e) => {
    e.preventDefault();
    //calling the logout action creator 
    this.props.logoutUserAction();
}

async componentDidMount() {
    //storing current user details
    var curruser = this.props.reducer_state.userReducer.currentuser;
    var dealer = this.props.reducer_state.userReducer.dealer;
    var customer = this.props.reducer_state.userReducer.customer;
    //getting data from db
    if (curruser != null) {
        var details = await items.find({}).toArray();
        //checking if the user details are present  in db or not
        var ispresent = details.some(item => item.owner_id === curruser.id);
       // console.log("checking is dealer:- " + dealer);
        var result = details.filter(item => item.owner_id === curruser.id);
        if (ispresent) {
            //setting the state values
            this.setState({
                isAdmin: result[0].isAdmin,
                isCustomer: result[0].isCustomer,
                isDealer: result[0].isDealer
            })
        }
        else{
            //setting the state values
            this.setState({
                isCustomer: customer,
                isDealer: dealer
            })
        }
    }
}

render() {
    return (
      <Router>
        <MDBContainer>
          <MDBBtn onClick={this.sidenavToggle("Left")}>
            <MDBIcon size="lg" icon="bars" />
          </MDBBtn>
          <MDBSideNav slim fixed mask="rgba-blue-strong" triggerOpening={this.state.sideNavLeft} breakWidth={1300}
            className="sn-bg-1">
            <li>
              <div className="logo-wrapper sn-ad-avatar-wrapper">
                <a href="#!">
                  <img alt="" src="https://mdbootstrap.com/img/Photos/Avatars/img%20(10).jpg" className="rounded-circle" />
                  <span>Anna Deynah</span>
                </a>
              </div>
            </li>

            <MDBSideNavNav>
              <MDBSideNavLink to="/other-page" topLevel>
                <MDBIcon icon="pencil-alt" className="mr-2" />Submit listing</MDBSideNavLink>
              <MDBSideNavCat name="Submit blog" id="submit-blog" icon="chevron-right">
                <MDBSideNavLink>Submit listing</MDBSideNavLink>
                <MDBSideNavLink>Registration form</MDBSideNavLink>
              </MDBSideNavCat>
              <MDBSideNavCat name="Instruction" id="instruction" icon="hand-pointer" href="#">
                <MDBSideNavLink>For bloggers</MDBSideNavLink>
                <MDBSideNavLink>For authors</MDBSideNavLink>
              </MDBSideNavCat>
              <MDBSideNavCat name="About" id="about" icon="eye">
                <MDBSideNavLink>Instruction</MDBSideNavLink>
                <MDBSideNavLink>Monthly meetings</MDBSideNavLink>
              </MDBSideNavCat>
              <MDBSideNavCat name="Contact me" id="contact-me" icon="envelope">
                <MDBSideNavLink>FAQ</MDBSideNavLink>
                <MDBSideNavLink>Write a message</MDBSideNavLink>
              </MDBSideNavCat>
            </MDBSideNavNav>
          </MDBSideNav>
        </MDBContainer>
      </Router>
    );
  }
}

export default SideNavPage;
