import React from 'react';
import {Form,Button} from 'react-bootstrap';
import updateProfile from './../Redux/Actions/UpdateProfile';
import {connect} from 'react-redux';
import { items } from "./../Stitch/mongodb";
import './../CSS/profile.css';

class Profile extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            name: '',
            age: 10,
            email: '',
            bio:''          
        }
    }

    async componentDidMount (){
        //getting data from db
        var details = await  items.find({}, { limit: 1000 }).toArray();
        //storing current user details
        var curruser = this.props.reducerState.userReducer.currentuser; 
        //checking if the user details are present  in db or not
        var ispresent = details.some(item => item.owner_id === curruser.id);
        if(ispresent){
            //if user details are present then filtering it out to a new array
            var result = details.filter( item => item.owner_id === curruser.id);
            //setting the state values
            this.setState({
                name: result[0].name,
                email: result[0].email,
                age: result[0].age,
                bio: result[0].bio
            })
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();
       // calling update profile action type
        this.props.updateProfile(this.state.name,this.state.email,this.state.age,this.state.bio);        
    }

    render(){
        
        return (
            <div className='profilecontainer'>
            <Form onSubmit={this.handleSubmit}>
                   
                    <Form.Group controlId="formBasicName">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text"  value={this.state.name} onChange={e => this.setState({name:e.target.value})}/>
                    </Form.Group>

                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email"  value={this.state.email} onChange={e => this.setState({email: e.target.value})}/>
                    </Form.Group>

                    <Form.Group controlId="formBasicName">
                        <Form.Label>Age</Form.Label>
                        <Form.Control type="number"  value={this.state.age} min={10} max={100} onChange={e => this.setState({age:e.target.value})}/>
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>About</Form.Label>
                        <Form.Control as="textarea" rows="3" value={this.state.bio} onChange={e => this.setState({bio:e.target.value})}/>
                    </Form.Group>
                    

                    <Button variant="primary" type="submit">
                        Update Profile
                    </Button>
                </Form>
                </div>
        );
    }
}

const mapStateToProps = (state) => ({
        reducerState: state      
})


const mapDispatchToProps = {
    updateProfile
}

export default connect(mapStateToProps, mapDispatchToProps)(Profile);
