import { RemoteMongoClient } from "mongodb-stitch-browser-sdk";
import { app } from "./app";


const mongoClient = app.getServiceClient(
    RemoteMongoClient.factory,
    "mongodb-atlas"
);

export const adminDetails = mongoClient.db("users").collection("admin");
export const dealerDetails = mongoClient.db("users").collection("delears");
export const customerDetails = mongoClient.db("users").collection("customers");
export const items = mongoClient.db("users").collection("details");
